CREATE TABLE graph(id INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL, name VARCHAR(16) NOT NULL, has_static_function INT NOT NULL, ignored_functions INTEGER, UNIQUE(name,has_static_function));
CREATE TABLE vertex(id INTEGER NOT NULL, name VARCHAR(32), graph_id INT REFERENCES graph(id) ON UPDATE CASCADE ON DELETE CASCADE,PRIMARY KEY(id,graph_id));
CREATE TABLE arc(from_id INT NOT NULL REFERENCES vertex(id) ON UPDATE CASCADE ON DELETE CASCADE, graph_id INTEGER NOT NULL REFERENCES graph(id) ON UPDATE CASCADE ON DELETE CASCADE, to_id INT NOT NULL REFERENCES vertex(id) ON UPDATE CASCADE ON DELETE CASCADE, weight REAL NOT NULL, PRIMARY KEY(graph_id,from_id, to_id));
